@import "tailwindcss";
@import "tw-animate-css";

@plugin "tailwindcss-animate";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));
  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));
  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));
  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));
  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));
  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));
  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));
  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));
  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));
  --color-chart-1: hsl(var(--chart-1));
  --color-chart-2: hsl(var(--chart-2));
  --color-chart-3: hsl(var(--chart-3));
  --color-chart-4: hsl(var(--chart-4));
  --color-chart-5: hsl(var(--chart-5));
  --color-sidebar: hsl(var(--sidebar));
  --color-sidebar-foreground: hsl(var(--sidebar-foreground));
  --color-sidebar-primary: hsl(var(--sidebar-primary));
  --color-sidebar-primary-foreground: hsl(var(--sidebar-primary-foreground));
  --color-sidebar-accent: hsl(var(--sidebar-accent));
  --color-sidebar-accent-foreground: hsl(var(--sidebar-accent-foreground));
  --color-sidebar-border: hsl(var(--sidebar-border));
  --color-sidebar-ring: hsl(var(--sidebar-ring));
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom Styles from User HTML */
body {
    font-family: 'Courier New', monospace;
    background: #000;
    color: #fff;
    overflow-x: hidden;
    min-height: 100vh;
    -webkit-tap-highlight-color: transparent;
}

/* Background Videos */
.bg-video {
    position: fixed;
    right: 0;
    bottom: 0;
    min-width: 100%;
    min-height: 100%;
    z-index: -100;
    opacity: 0.9;
    filter: brightness(0.9);
    object-fit: cover;
}

.md-bg-video {
    position: fixed;
    right: 0;
    bottom: 0;
    min-width: 100%;
    min-height: 100%;
    z-index: -100;
    opacity: 0.8;
    object-fit: cover;
}

/* Navigation */
nav {
    position: fixed;
    top: 15px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    flex-wrap: nowrap;
    overflow-x: auto;
    gap: 8px;
    background: rgba(0, 0, 0, 0.85);
    padding: 10px 15px;
    border-radius: 25px;
    backdrop-filter: blur(15px);
    z-index: 1000;
    border: 1px solid rgba(255, 255, 255, 0.15);
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.6);
    max-width: 95vw;
    white-space: nowrap;
    -webkit-overflow-scrolling: touch;
    scrollbar-width: none;
}

nav::-webkit-scrollbar {
    display: none;
}

.nav-btn {
    background: transparent;
    border: none;
    color: #fff;
    padding: 8px 16px;
    border-radius: 20px;
    cursor: pointer;
    font-family: 'Courier New', monospace;
    font-size: 14px;
    transition: all 0.3s;
    white-space: nowrap;
    flex-shrink: 0;
}

.nav-btn:hover {
    background: rgba(255, 255, 255, 0.15);
    transform: translateY(-2px);
}

.nav-btn.active {
    background: rgba(255, 255, 255, 0.25);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow: 0 0 15px rgba(255, 255, 255, 0.2);
}

/* Bottom Ticker */
.ticker-container {
    position: fixed;
    bottom: 20px;
    left: 0;
    width: 100%;
    height: 35px;
    overflow: hidden;
    z-index: 900;
    background: rgba(0, 0, 0, 0.75);
    backdrop-filter: blur(5px);
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.ticker {
    display: flex;
    animation: ticker 40s linear infinite;
    white-space: nowrap;
    padding: 8px 0;
}

.ticker-item {
    padding: 0 25px;
    color: #fff;
    font-size: 14px;
    opacity: 0.9;
    font-weight: 500;
}

.ticker-item:nth-child(odd) {
    color: #00a8ff;
    text-shadow: 0 0 8px rgba(0, 168, 255, 0.5);
}

.ticker-item:nth-child(even) {
    color: #ff6b6b;
    text-shadow: 0 0 8px rgba(255, 107, 107, 0.5);
}

/* Content Sections */
.section-container {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 90%;
    max-width: 900px;
    max-height: 75vh;
    overflow-y: auto;
    padding: 25px;
    background: rgba(0, 0, 0, 0.9);
    border-radius: 12px;
    border: 1px solid rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(15px);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.6);
    z-index: 100;
}

.section-enter {
    animation: fadeIn 0.5s ease;
}

/* Home Page - Cybersecurity Tips */
.tip-container {
    min-height: 300px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 30px 20px;
    text-align: center;
    position: relative;
}

.cyber-tip {
    font-size: 22px;
    color: #00a8ff;
    margin-bottom: 20px;
    opacity: 0;
    transition: opacity 1s ease;
    position: absolute;
    width: 100%;
    text-align: center;
    padding: 0 10px;
}

.cyber-tip.show {
    opacity: 1;
}

.squirrel {
    font-size: 60px;
    margin-top: 30px;
    opacity: 0;
    transition: opacity 0.5s ease;
    position: absolute;
    bottom: 20px;
}

.squirrel.show {
    opacity: 1;
    animation: bounce 2s infinite;
}

/* Contact Icons - Real Colors */
.contact-icons {
    position: fixed;
    bottom: 70px;
    right: 20px;
    display: flex;
    flex-direction: column;
    gap: 12px;
    z-index: 1000;
}

.contact-icons-enter {
    animation: slideIn 0.5s ease;
}

.icon {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 22px;
    color: white;
    text-decoration: none;
    background: rgba(0, 0, 0, 0.8);
    backdrop-filter: blur(10px);
    border: 2px solid;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
}

.icon:hover {
    transform: scale(1.15) rotate(5deg);
    box-shadow: 0 0 20px currentColor;
}

/* Real Brand Colors */
.whatsapp { 
    border-color: #25D366;
    background: #25D366;
}
.telegram { 
    border-color: #0088cc;
    background: #0088cc;
}
.instagram { 
    border-color: #E4405F;
    background: linear-gradient(45deg, #405DE6, #833AB4, #C13584, #E1306C, #FD1D1D);
}
.youtube { 
    border-color: #FF0000;
    background: #FF0000;
}
.tiktok { 
    border-color: #69C9D0;
    background: #000;
    background: linear-gradient(45deg, #69C9D0, #EE1D52);
}

.icon i {
    filter: drop-shadow(0 2px 3px rgba(0,0,0,0.3));
}

/* Catalogue Styles */
.catalogue-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: 15px;
    margin-top: 20px;
}

.product-item {
    background: rgba(30, 30, 40, 0.9);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s;
}

.product-item:hover {
    border-color: rgba(0, 168, 255, 0.5);
    transform: translateY(-3px);
    box-shadow: 0 8px 20px rgba(0, 168, 255, 0.2);
}

.product-title {
    color: #00a8ff;
    margin-bottom: 8px;
    font-size: 16px;
    font-weight: bold;
}

.product-price {
    color: #ff6b6b;
    font-size: 22px;
    margin: 12px 0;
    font-weight: bold;
}

.cart-btn {
    width: 100%;
    padding: 10px;
    background: rgba(0, 168, 255, 0.2);
    border: 1px solid rgba(0, 168, 255, 0.4);
    color: white;
    cursor: pointer;
    border-radius: 5px;
    transition: all 0.3s;
    font-family: 'Courier New', monospace;
    font-weight: bold;
}

.cart-btn:hover {
    background: rgba(0, 168, 255, 0.4);
    transform: translateY(-2px);
}

.cart-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

/* Login Character */
.character {
    position: absolute;
    right: -60px;
    bottom: -40px;
    width: 180px;
    height: 180px;
    opacity: 0.6;
    pointer-events: none;
}

.eye {
    position: absolute;
    width: 14px;
    height: 14px;
    background: #00a8ff;
    border-radius: 50%;
    top: 45%;
    left: 50%;
    transform: translate(-50%, -50%);
    transition: height 0.3s;
    box-shadow: 0 0 15px #00a8ff;
}

.eye.blink {
    height: 3px;
}

/* MD Page Snow */
.snowflake {
    position: fixed;
    color: #00a8ff;
    font-size: 16px;
    z-index: 50;
    opacity: 0.8;
    pointer-events: none;
    text-shadow: 0 0 10px #00a8ff;
}

/* Bug Page Effects */
.matrix-bg {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #000;
    z-index: -1;
    opacity: 0.3;
}

.glitch-text {
    position: relative;
    color: #ff6b6b;
    animation: glitch 0.5s infinite;
}

/* Music Control Button */
.music-control {
    position: fixed;
    top: 20px;
    left: 20px;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.2);
    color: #00a8ff;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    z-index: 1001;
    backdrop-filter: blur(10px);
    transition: all 0.3s;
}

.music-control:hover {
    background: rgba(0, 168, 255, 0.2);
    transform: scale(1.1);
}

/* Checkout Modal */
.checkout-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    z-index: 2000;
    display: flex;
    align-items: center;
    justify-content: center;
}

.checkout-modal-enter {
    animation: fadeIn 0.3s ease;
}

.checkout-content {
    background: rgba(20, 20, 30, 0.95);
    width: 90%;
    max-width: 500px;
    border-radius: 15px;
    padding: 30px;
    border: 2px solid rgba(0, 168, 255, 0.3);
    box-shadow: 0 15px 50px rgba(0, 0, 0, 0.7);
}

.close-checkout {
    float: right;
    font-size: 28px;
    color: #ff6b6b;
    cursor: pointer;
    transition: all 0.3s;
}

.close-checkout:hover {
    transform: rotate(90deg);
}

/* Payment Methods */
.payment-options {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 15px;
    margin: 25px 0;
}

.payment-method {
    background: rgba(40, 40, 50, 0.8);
    border-radius: 10px;
    padding: 20px;
    text-align: center;
    cursor: pointer;
    transition: all 0.3s;
    border: 2px solid transparent;
}

.payment-method:hover {
    transform: translateY(-5px);
    border-color: #00a8ff;
    box-shadow: 0 10px 25px rgba(0, 168, 255, 0.3);
}

.payment-icon {
    font-size: 40px;
    margin-bottom: 10px;
}

.mpesa-icon {
    color: #00A300;
}

.airtel-icon {
    color: #FF0000;
}

.international-icon {
    color: #FFD700;
}

/* Payment Pages Styling */
.payment-page {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #000;
    z-index: 3000;
    overflow-y: auto;
    padding: 20px;
}

.payment-header {
    text-align: center;
    margin-bottom: 30px;
    padding: 20px;
    background: rgba(0, 0, 0, 0.7);
    border-radius: 10px;
    border-bottom: 3px solid;
}

.mpesa-header {
    border-color: #00A300;
}

.airtel-header {
    border-color: #FF0000;
}

.payment-content {
    max-width: 600px;
    margin: 0 auto;
    background: rgba(30, 30, 40, 0.9);
    padding: 30px;
    border-radius: 15px;
    border: 2px solid rgba(255, 255, 255, 0.1);
}

.payment-status {
    text-align: center;
    margin: 30px 0;
    padding: 20px;
    border-radius: 10px;
    font-size: 18px;
    font-weight: bold;
}

.processing {
    background: rgba(255, 215, 0, 0.2);
    border: 2px solid #FFD700;
    color: #FFD700;
}

.success {
    background: rgba(0, 163, 0, 0.2);
    border: 2px solid #00A300;
    color: #00A300;
}

.receipt-container {
    background: #000;
    border: 3px solid #00a8ff;
    border-radius: 10px;
    padding: 25px;
    margin: 30px 0;
    font-family: monospace;
    position: relative;
}

.receipt-title {
    text-align: center;
    color: #00a8ff;
    font-size: 24px;
    margin-bottom: 20px;
    border-bottom: 2px dashed #00a8ff;
    padding-bottom: 10px;
}

.receipt-item {
    display: flex;
    justify-content: space-between;
    margin-bottom: 15px;
    padding: 10px;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 5px;
}

.copy-btn {
    background: rgba(0, 168, 255, 0.3);
    border: 1px solid #00a8ff;
    color: white;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    font-family: 'Courier New', monospace;
    font-weight: bold;
    transition: all 0.3s;
    margin-top: 20px;
    width: 100%;
}

.copy-btn:hover {
    background: rgba(0, 168, 255, 0.5);
    transform: translateY(-2px);
}

.back-btn {
    background: rgba(255, 107, 107, 0.3);
    border: 1px solid #ff6b6b;
    color: white;
    padding: 12px 30px;
    border-radius: 5px;
    cursor: pointer;
    font-family: 'Courier New', monospace;
    font-weight: bold;
    transition: all 0.3s;
    margin-top: 30px;
    font-size: 16px;
}

.back-btn:hover {
    background: rgba(255, 107, 107, 0.5);
    transform: translateY(-2px);
}

/* Animations */
@keyframes ticker {
    0% {
        transform: translateX(100%);
    }
    100% {
        transform: translateX(-100%);
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translate(-50%, -45%);
    }
    to {
        opacity: 1;
        transform: translate(-50%, -50%);
    }
}

@keyframes glitch {
    0% { transform: translate(0); }
    20% { transform: translate(-2px, 2px); }
    40% { transform: translate(2px, -2px); }
    60% { transform: translate(-2px, 1px); }
    80% { transform: translate(2px, -1px); }
    100% { transform: translate(0); }
}

@keyframes fall {
    to {
        transform: translateY(100vh);
    }
}

@keyframes bounce {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
}

@keyframes slideIn {
    from {
        transform: translateX(100px);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

/* Mobile Responsive */
@media (max-width: 768px) {
    nav {
        top: 10px;
        padding: 8px 12px;
        gap: 6px;
        max-width: 90vw;
        overflow-x: auto;
    }

    .nav-btn {
        padding: 6px 12px;
        font-size: 12px;
        min-width: max-content;
    }

    .section-container {
        width: 95%;
        padding: 20px;
        max-height: 80vh;
        top: 55%;
    }

    .catalogue-grid {
        grid-template-columns: 1fr;
        gap: 12px;
    }

    .ticker-item {
