    }

    .contact-icons {
        bottom: 60px;
        right: 15px;
        gap: 10px;
    }

    .icon {
        width: 45px;
        height: 45px;
        font-size: 20px;
    }

    .music-control {
        top: 15px;
        left: 15px;
        width: 35px;
        height: 35px;
        font-size: 14px;
    }

    .character {
        right: -40px;
        bottom: -30px;
        width: 140px;
        height: 140px;
    }

    .product-title {
        font-size: 15px;
    }

    .product-price {
        font-size: 20px;
    }

    .checkout-content {
        width: 95%;
        padding: 20px;
    }

    .payment-options {
        grid-template-columns: 1fr;
    }

    .payment-content {
        padding: 20px;
    }
}

@media (max-width: 480px) {
    nav {
        padding: 6px 10px;
        gap: 4px;
    }

    .nav-btn {
        padding: 5px 10px;
        font-size: 11px;
    }

    .cyber-tip {
        font-size: 16px;
    }

    .section-container {
        padding: 15px;
    }

    .ticker-item {
        font-size: 11px;
        padding: 0 12px;
    }

    .music-control {
        top: 12px;
        left: 12px;
    }
}

/* Landscape mode for mobile */
@media (max-height: 600px) and (orientation: landscape) {
    nav {
        top: 5px;
        padding: 6px 10px;
    }

    .section-container {
        top: 50%;
        max-height: 85vh;
    }

    .ticker-container {
        bottom: 10px;
        height: 30px;
    }

